export function MainPage({news}){
    const [selectedArticle, setSelectedArticle] = useState(null);

    const showArticle = (article) => {
        setSelectedArticle(article);
    }

    const hideArticle = () => {
        setSelectedArticle(null);
    }

    return (
        <div
          style={{
            height: "100vh", // Make it take full viewport height
            display: "flex",
            flexDirection: "column",
          }}
        >
          <h2 style={{ textAlign: "center", padding: "1rem" }}>News Articles</h2>
          <div
            style={{
              flex: 1, // Takes all available space
              overflowY: "auto", // Enables scrolling
              padding: "1rem",
            }}
          >
            {news.length === 0 ? (
              <p>Loading articles...</p>
            ) : (
              news.map((article, index) => (
                <div
                  key={index}
                  style={{
                    border: "1px solid #ddd",
                    padding: "1rem",
                    marginBottom: "1rem",
                    borderRadius: "8px",
                    boxShadow: "0px 2px 5px rgba(0,0,0,0.1)",
                    cursor: "pointer",
                    transition: "transform 0.3s ease-in-out", // Smooth transition for hover effect
                  }}
                  onMouseEnter={() => showArticle(article)} // Show details on hover
                  onMouseLeave={hideArticle} // Hide details when mouse leaves
                >
                  <h3>{article.title}</h3>
                  <p>{article.description}</p>
                  <a href={article.url} target="_blank" rel="noopener noreferrer">
                    Read more
                  </a>
                </div>
              ))
            )}
          </div>

          {/* Full-screen Modal for Detailed Article */}
          {selectedArticle && (
            <div
              style={{
                position: "fixed",
                top: 0,
                left: 0,
                width: "100vw",
                height: "100vh",
                backgroundColor: "rgba(0, 0, 0, 0.7)", // Dark background overlay
                color: "white",
                padding: "2rem",
                overflowY: "auto",
                zIndex: 1000,
              }}
            >
              <button
                onClick={hideArticle}
                style={{
                  position: "absolute",
                  top: "1rem",
                  right: "1rem",
                  backgroundColor: "transparent",
                  border: "none",
                  color: "white",
                  fontSize: "1.5rem",
                  cursor: "pointer",
                }}
              >
                X
              </button>
              <h2>{selectedArticle.title}</h2>
              <p>{selectedArticle.content}</p>
              <a href={selectedArticle.url} target="_blank" rel="noopener noreferrer">
                Read more
              </a>
            </div>
          )}
        </div>
      );
}
